ALTER COLUMN id INT;
UPDATE stage.users
SET phone = REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(phone, '-', ''), '(', ''), ')', ''), ' ', ''), '.', ''), '+', ''), '/', ''), '\\', ''), '*', ''), '#', '');
UPDATE stage.users
SET phone =REPLACE(phone, 'x', '');
